insert  into `ekh_content`(`id`,`asset_id`,`title`,`alias`,`introtext`,`fulltext`,`state`,`catid`,`created`,`created_by`,`created_by_alias`,`modified`,`modified_by`,`checked_out`,`checked_out_time`,`publish_up`,`publish_down`,`images`,`urls`,`attribs`,`version`,`ordering`,`metakey`,`metadesc`,`access`,`hits`,`metadata`,`featured`,`language`,`xreference`) values (31,103,'Képviselő választása, megbízás visszavonása','kepviselo-valasztasa-megbizas-visszavonasa','<p>&nbsp;A témakörök listáját tartalmazó képernyőn <strong>általános képviselő</strong> választására, egy adott témakör szavazaásait tartalmazó képernyőn <strong>témakör képviselő</strong> választására van lehetőség a képernyők jobb felső részén lévő gombra kattintva. Természetesen ez a lehetőség csak akkor áll fen, ha még nincs képviselő kijelölve, és a felhasználó saját maga nem jelentkezett képviselőnek (a képviselők ugyanis nem ruházhatják át másra a szavazati jogukat).</p>\r\n<p>A gombra kattintva azon regisztrált felhasználók listája jelenik meg akik az adott szintű képviseletre vállalkoztak. A név melletti \"Információ\" ikonra kattintva megtekinthető a jelölt bemutatkozó szövege, programja, látható, hogy a jelölt jelenleg kiket képvisel és mód van az általa eddig leadott szavazatok megtekintésére is. (kivéve a szigoruan titkos szavazásokat, ezeknél senkinek a szavazata nem kérdethető le). A kiválasztott jelölt nevére kattintva történik meg a képviseleti megbízás.</p>\r\n<p>Ha már van az adott szinten képviselőnk akkor a \"képviselő\" feliratú gombra kattintva lekérdezhető a képviselő bemutatkozó szövege,átható, hogy a jelölt jelenleg kiket képvisel és mód van az általa eddig leadott szavazatok megtekintésére is. (kivéve a szigoruan titkos szavazásokat, ezeknél senkinek a szavazata nem kérdethető le). Ezen a képernyőn van egy \"<strong>képviseleti megbizás visszavonása</strong>\" gomba amire kattintva visszavonhatjuk a képviseleti megbízásunkat.</p>\r\n<p>A képviselő jelöltek és képviselők adatait lekérdező képernyőn lehetőségünk van e-mail -t küldeni az adott képviselőnek vagy képviselő jelöltnek.</p>\r\n<p>&nbsp;</p>\r\n<p style=\"text-align: center;\">{loadposition back}</p>','',1,9,'2014-05-16 05:16:22',50,'','0000-00-00 00:00:00',0,0,'0000-00-00 00:00:00','2014-05-16 05:16:22','0000-00-00 00:00:00','{\"image_intro\":\"\",\"float_intro\":\"\",\"image_intro_alt\":\"\",\"image_intro_caption\":\"\",\"image_fulltext\":\"\",\"float_fulltext\":\"\",\"image_fulltext_alt\":\"\",\"image_fulltext_caption\":\"\"}','{\"urla\":false,\"urlatext\":\"\",\"targeta\":\"\",\"urlb\":false,\"urlbtext\":\"\",\"targetb\":\"\",\"urlc\":false,\"urlctext\":\"\",\"targetc\":\"\"}','{\"show_title\":\"\",\"link_titles\":\"\",\"show_tags\":\"\",\"show_intro\":\"\",\"info_block_position\":\"\",\"show_category\":\"\",\"link_category\":\"\",\"show_parent_category\":\"\",\"link_parent_category\":\"\",\"show_author\":\"\",\"link_author\":\"\",\"show_create_date\":\"\",\"show_modify_date\":\"\",\"show_publish_date\":\"\",\"show_item_navigation\":\"\",\"show_icons\":\"\",\"show_print_icon\":\"\",\"show_email_icon\":\"\",\"show_vote\":\"\",\"show_hits\":\"\",\"show_noauth\":\"\",\"urls_position\":\"\",\"alternative_readmore\":\"\",\"article_layout\":\"\",\"show_publishing_options\":\"\",\"show_article_options\":\"\",\"show_urls_images_backend\":\"\",\"show_urls_images_frontend\":\"\"}',1,0,'','',1,3,'{\"robots\":\"\",\"author\":\"\",\"rights\":\"\",\"xreference\":\"\"}',0,'*','');
